mapper.select:
SELECT uniq_Id, user_Ty, mber_Id , user_Nm, email_Adres, area_No, middle_Telno, end_Telno, moblphon_No, group_Id, sttus, sbscrb_De FROM( SELECT ESNTL_ID uniq_Id, 'USR01' user_Ty, MBER_ID mber_Id, MBER_NM user_Nm, MBER_EMAIL_ADRES email_Adres, AREA_NO area_No, MIDDLE_TELNO middle_Telno, END_TELNO end_Telno, MBTLNUM moblphon_No, GROUP_ID group_Id, MBER_STTUS sttus, VARCHAR_FORMAT(SBSCRB_DE,'YYYY-MM-DD') sbscrb_De FROM LETTNGNRLMBER ) A WHERE 1=1
AND sttus LIKE #{sbscrbSttus}
AND mber_Id LIKE '${searchKeyword}'
AND user_Nm LIKE '%' || '${searchKeyword}' || '%'
ORDER BY sbscrb_De DESC LIMIT #{recordCountPerPage} OFFSET #{firstIndex}

mapper.update:

UPDATE LETTNGNRLMBER 
SET 
       PASSWORD   =  #{password}
WHERE  ESNTL_ID  = #{uniqId}
